import React, { useState, useEffect, useRef } from 'react';
import { Lock, CheckCircle, XCircle, AlertTriangle, Loader, Search, Copy, StopCircle, Play, User, Phone, Hash } from 'lucide-react';
import FileUpload from '../components/FileUpload';
import { sessionAPI } from '../services/api';
import toast from 'react-hot-toast';

const AuthCodeExtractor = () => {
  const [files, setFiles] = useState([]);
  const [sessionCards, setSessionCards] = useState([]);
  const [loading, setLoading] = useState(false);
  const [monitoring, setMonitoring] = useState(false);
  const [extractedCodes, setExtractedCodes] = useState([]);
  const wsRef = useRef(null);

  const handleFilesSelected = (selectedFiles) => {
    setFiles(selectedFiles);
    setSessionCards([]);
    setExtractedCodes([]);
    
    // Create session cards for each file
    const cards = selectedFiles.map(file => ({
      file: file,
      filename: file.name,
      status: 'pending',
      accountInfo: null,
      monitoring: false,
      taskId: null,
      codes: []
    }));
    
    setSessionCards(cards);
  };

  const handleScanCode = async (cardIndex) => {
    const card = sessionCards[cardIndex];
    if (card.status === 'scanning') return;

    // Update card status
    const updatedCards = [...sessionCards];
    updatedCards[cardIndex] = { ...card, status: 'scanning' };
    setSessionCards(updatedCards);

    try {
      // First scan account info
      const scanResponse = await sessionAPI.scanAuthAccountInfo([card.file]);
      console.log('Auth scan response:', scanResponse); // Debug log
      
      const scanResult = scanResponse.results[0];
      console.log('Auth scan result:', scanResult); // Debug log

      if (scanResult.status === 'success') {
        // Create a completely new array to ensure React detects the change
        const newSessionCards = [...sessionCards];
        newSessionCards[cardIndex] = {
          ...card,
          status: 'scanned',
          accountInfo: scanResult,
          monitoring: false
        };
        
        console.log('Setting auth account info in state:', newSessionCards[cardIndex]); // Debug log
        setSessionCards(newSessionCards);
        toast.success(`Account info extracted for ${card.filename}`);
        console.log('Updated auth card with account info:', newSessionCards[cardIndex]); // Debug log
      } else {
        // Handle error
        const newSessionCards = [...sessionCards];
        newSessionCards[cardIndex] = {
          ...card,
          status: 'error',
          error: scanResult.error
        };
        setSessionCards(newSessionCards);
        toast.error(`Failed to scan ${card.filename}: ${scanResult.error}`);
      }
    } catch (error) {
      console.error('Auth scan error:', error);
      const newSessionCards = [...sessionCards];
      newSessionCards[cardIndex] = {
        ...card,
        status: 'error',
        error: 'Failed to scan account'
      };
      setSessionCards(newSessionCards);
      toast.error(`Failed to scan ${card.filename}`);
    }
  };

  const handleStartMonitoring = async (cardIndex) => {
    const card = sessionCards[cardIndex];
    if (!card.accountInfo || card.accountInfo.status !== 'success') {
      toast.error('Please scan the account first');
      return;
    }

    // Update card to show monitoring is starting
    const newSessionCards = [...sessionCards];
    newSessionCards[cardIndex] = { ...card, monitoring: true };
    setSessionCards(newSessionCards);

    try {
      // Start monitoring
      const monitorResponse = await sessionAPI.startAuthCodeMonitoring([card.file]);
      
      // The backend returns { task_id, message, session_files } not { results }
      if (monitorResponse.task_id) {
        // Update card with monitoring info
        const updatedSessionCards = [...sessionCards];
        updatedSessionCards[cardIndex] = {
          ...card,
          monitoring: true,
          taskId: monitorResponse.task_id
        };
        setSessionCards(updatedSessionCards);
        setMonitoring(true);
        toast.success(`Started monitoring ${card.filename}`);
        
        // Connect to WebSocket for real-time updates
        connectWebSocket(monitorResponse.task_id);
      } else {
        // Handle error
        const updatedSessionCards = [...sessionCards];
        updatedSessionCards[cardIndex] = {
          ...card,
          monitoring: false,
          error: monitorResponse.error || 'Failed to start monitoring'
        };
        setSessionCards(updatedSessionCards);
        toast.error(`Failed to start monitoring ${card.filename}: ${monitorResponse.error || 'Unknown error'}`);
      }
    } catch (error) {
      console.error('Monitoring error:', error);
      const updatedSessionCards = [...sessionCards];
      updatedSessionCards[cardIndex] = {
        ...card,
        monitoring: false,
        error: 'Failed to start monitoring'
      };
      setSessionCards(updatedSessionCards);
      toast.error(`Failed to start monitoring ${card.filename}`);
    }
  };

  const handleStopMonitoring = async (cardIndex) => {
    const card = sessionCards[cardIndex];
    if (!card.monitoring) return;

    try {
      // Stop monitoring
      await sessionAPI.stopAuthCodeMonitoring([card.filename]);
      
      // Update card status
      const updatedCards = [...sessionCards];
      updatedCards[cardIndex] = {
        ...card,
        monitoring: false,
        taskId: null
      };
      setSessionCards(updatedCards);
      
      // Close WebSocket
      if (wsRef.current) {
        wsRef.current.close();
        wsRef.current = null;
      }
      
      toast.success(`Stopped monitoring ${card.filename}`);
    } catch (error) {
      console.error('Stop monitoring error:', error);
      toast.error(`Failed to stop monitoring ${card.filename}`);
    }
  };

  const connectWebSocket = (taskId) => {
    const wsUrl = `ws://localhost:8000/ws/stream`;
    const ws = new WebSocket(wsUrl);
    
    ws.onopen = () => {
      console.log('WebSocket connected');
      ws.send(JSON.stringify({ task_id: taskId }));
    };
    
    ws.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);
        
        if (data.type === 'auth_code_found') {
          // Add new code to the list
          const newCode = {
            id: Date.now(),
            session: data.session,
            user_id: data.user_id,
            code: data.code,
            message_id: data.message_id,
            date: data.date,
            message_text: data.message_text
          };
          
          setExtractedCodes(prev => [newCode, ...prev]);
          toast.success(`New auth code found: ${data.code}`);
        }
      } catch (error) {
        console.error('WebSocket message error:', error);
      }
    };
    
    ws.onerror = (error) => {
      console.error('WebSocket error:', error);
    };
    
    ws.onclose = () => {
      console.log('WebSocket disconnected');
    };
    
    wsRef.current = ws;
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    toast.success('Code copied to clipboard!');
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'success':
      case 'scanned':
        return <CheckCircle className="h-5 w-5 text-green-500" />;
      case 'error':
        return <XCircle className="h-5 w-5 text-red-500" />;
      case 'scanning':
        return <Loader className="h-5 w-5 text-blue-500 animate-spin" />;
      case 'pending':
        return <AlertTriangle className="h-5 w-5 text-yellow-500" />;
      default:
        return <AlertTriangle className="h-5 w-5 text-gray-500" />;
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'success':
      case 'scanned':
        return 'border-green-200 bg-green-50';
      case 'error':
        return 'border-red-200 bg-red-50';
      case 'scanning':
        return 'border-blue-200 bg-blue-50';
      case 'pending':
        return 'border-yellow-200 bg-yellow-50';
      default:
        return 'border-gray-200 bg-gray-50';
    }
  };

  // Cleanup WebSocket on unmount
  useEffect(() => {
    return () => {
      if (wsRef.current) {
        wsRef.current.close();
      }
    };
  }, []);

  // Debug: Monitor sessionCards state changes
  useEffect(() => {
    console.log('Auth SessionCards state updated:', sessionCards);
    sessionCards.forEach((card, index) => {
      if (card.status === 'scanned' && card.accountInfo) {
        console.log(`Auth Card ${index} has account info:`, card.accountInfo);
      }
    });
  }, [sessionCards]);

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center space-x-3">
        <Lock className="h-8 w-8 text-telegram-500" />
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Auth Code Scanner</h1>
          <p className="text-gray-600">Scan accounts and monitor for incoming auth codes</p>
        </div>
      </div>

      {/* Info Card */}
      <div className="card bg-blue-50 border-blue-200">
        <div className="flex items-start space-x-3">
          <Search className="h-6 w-6 text-blue-600 mt-0.5" />
          <div>
            <h3 className="font-medium text-blue-900">How it works</h3>
            <p className="text-sm text-blue-700 mt-1">
              1. Upload session files to see account cards<br/>
              2. Click "Scan Code" to extract account information<br/>
              3. Click "Start Monitoring" to watch for incoming auth codes in chat 777000<br/>
              4. Codes will appear in real-time with copy buttons
            </p>
          </div>
        </div>
      </div>

      {/* File Upload */}
      <div className="card">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">Upload Session Files</h2>
        <FileUpload
          onFilesSelected={handleFilesSelected}
          acceptedFileTypes={['.session']}
          title="Upload Session Files"
          description="Drag & drop .session files here, or click to select files"
        />
      </div>

      {/* Session Cards */}
      {sessionCards.length > 0 && (
        <div className="card">
          <h2 className="text-lg font-semibold text-gray-900 mb-4">
            Session Cards ({sessionCards.length} files)
          </h2>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {sessionCards.map((card, index) => (
              <div
                key={index}
                className={`p-4 rounded-lg border ${getStatusColor(card.status)}`}
              >
                <div className="flex items-center justify-between mb-3">
                  <div className="flex items-center space-x-2">
                    {getStatusIcon(card.status)}
                    <h3 className="font-medium text-gray-900 text-sm truncate">
                      {card.filename}
                    </h3>
                  </div>
                </div>

                {/* Account Info */}
                {card.accountInfo && (
                  <div className="space-y-2 mb-3">
                    <div className="flex items-center space-x-2 text-xs">
                      <User className="h-3 w-3 text-gray-500" />
                      <span className="text-gray-700">
                        {card.accountInfo.first_name} {card.accountInfo.last_name}
                      </span>
                    </div>
                    <div className="flex items-center space-x-2 text-xs">
                      <Hash className="h-3 w-3 text-gray-500" />
                      <span className="text-gray-700">ID: {card.accountInfo.user_id}</span>
                    </div>
                    {card.accountInfo.phone && (
                      <div className="flex items-center space-x-2 text-xs">
                        <Phone className="h-3 w-3 text-gray-500" />
                        <span className="text-gray-700">{card.accountInfo.phone}</span>
                      </div>
                    )}
                    {/* Debug info */}
                    <div className="text-xs text-blue-600 bg-blue-50 p-1 rounded">
                      Debug: Account info exists - User ID: {card.accountInfo.user_id}
                    </div>
                  </div>
                )}
                
                {/* Debug: Show if accountInfo is missing */}
                {!card.accountInfo && card.status === 'scanned' && (
                  <div className="text-xs text-red-600 bg-red-50 p-1 rounded mb-3">
                    Debug: No account info found for scanned card
                  </div>
                )}

                {/* Error Message */}
                {card.error && (
                  <div className="mb-3">
                    <p className="text-xs text-red-600">{card.error}</p>
                  </div>
                )}

                {/* Action Buttons */}
                <div className="flex space-x-2">
                  {card.status === 'pending' && (
                    <button
                      onClick={() => handleScanCode(index)}
                      className="btn-primary flex-1 text-xs py-2"
                    >
                      <Search className="h-3 w-3 mr-1" />
                      Scan Code
                    </button>
                  )}
                  
                  {card.status === 'scanned' && !card.monitoring && (
                    <button
                      onClick={() => handleStartMonitoring(index)}
                      className="btn-primary flex-1 text-xs py-2"
                    >
                      <Play className="h-3 w-3 mr-1" />
                      Start Monitoring
                    </button>
                  )}
                  
                  {card.monitoring && (
                    <button
                      onClick={() => handleStopMonitoring(index)}
                      className="btn-secondary flex-1 text-xs py-2"
                    >
                      <StopCircle className="h-3 w-3 mr-1" />
                      Stop Monitoring
                    </button>
                  )}
                  
                  {card.status === 'scanning' && (
                    <div className="flex-1 text-xs py-2 text-center text-gray-500">
                      <Loader className="h-3 w-3 animate-spin inline mr-1" />
                      Scanning...
                    </div>
                  )}
                </div>

                {/* Monitoring Status */}
                {card.monitoring && (
                  <div className="mt-2 text-xs text-green-600 flex items-center">
                    <div className="w-2 h-2 bg-green-500 rounded-full mr-2 animate-pulse"></div>
                    Monitoring chat 777000
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Extracted Codes */}
      {extractedCodes.length > 0 && (
        <div className="card">
          <h2 className="text-lg font-semibold text-gray-900 mb-4">
            Extracted Auth Codes ({extractedCodes.length})
          </h2>
          
          <div className="space-y-3">
            {extractedCodes.map((code) => (
              <div key={code.id} className="p-4 bg-gray-50 rounded-lg border">
                <div className="flex items-center justify-between">
                  <div className="flex-1">
                    <div className="flex items-center space-x-3 mb-2">
                      <span className="font-mono text-lg font-bold text-gray-900">
                        {code.code}
                      </span>
                      <button
                        onClick={() => copyToClipboard(code.code)}
                        className="btn-secondary text-xs py-1 px-2"
                      >
                        <Copy className="h-3 w-3 mr-1" />
                        Copy
                      </button>
                    </div>
                    <div className="text-xs text-gray-600 space-y-1">
                      <p>Session: {code.session}</p>
                      <p>User ID: {code.user_id}</p>
                      {code.date && (
                        <p>Date: {new Date(code.date).toLocaleString()}</p>
                      )}
                    </div>
                  </div>
                </div>
                
                {/* Message Preview */}
                {code.message_text && (
                  <div className="mt-3 p-2 bg-white rounded border text-xs text-gray-700">
                    <p className="font-medium mb-1">Message Preview:</p>
                    <p className="whitespace-pre-wrap">{code.message_text}</p>
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Monitoring Status */}
      {monitoring && (
        <div className="card bg-green-50 border-green-200">
          <div className="flex items-center space-x-3">
            <div className="w-3 h-3 bg-green-500 rounded-full animate-pulse"></div>
            <div>
              <h3 className="font-medium text-green-900">Active Monitoring</h3>
              <p className="text-sm text-green-700">
                Watching for incoming auth codes in chat 777000. New codes will appear automatically.
              </p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AuthCodeExtractor;
