import React, { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';
import { Upload, X, FileText, AlertCircle, Archive, Check, Trash2 } from 'lucide-react';
import toast from 'react-hot-toast';
import { sessionAPI } from '../services/api';

const FileUpload = ({ 
  onFilesSelected, 
  acceptedFileTypes = ['.session', '.zip'], 
  multiple = true, 
  maxFiles = 10,
  title = "Upload Session Files or ZIP",
  description = "Drag & drop session files or ZIP files here, or click to select files"
}) => {
  const [files, setFiles] = useState([]);
  const [extractedSessions, setExtractedSessions] = useState([]);
  const [selectedSessions, setSelectedSessions] = useState([]);
  const [isProcessingZip, setIsProcessingZip] = useState(false);

  const processZipFile = async (zipFile) => {
    setIsProcessingZip(true);
    try {
      const response = await sessionAPI.previewZip(zipFile);
      const sessionFiles = response.session_files || [];
      
      // Extract the actual session files from ZIP
      const extractedResponse = await sessionAPI.extractSessionFiles(zipFile);
      const extractedFiles = extractedResponse.files || [];
      
      console.log('Backend extracted files response:', extractedResponse);
      console.log('Extracted files array:', extractedFiles);
      
      // Create File objects from the extracted session files
      const sessionFileObjects = extractedFiles.map(session => {
        try {
          console.log(`Processing session file: ${session.filename}, size: ${session.size}`);
          
          // Decode base64 content back to binary
          const binaryContent = atob(session.content);
          const bytes = new Uint8Array(binaryContent.length);
          for (let i = 0; i < binaryContent.length; i++) {
            bytes[i] = binaryContent.charCodeAt(i);
          }
          
          const blob = new Blob([bytes], { type: 'application/octet-stream' });
          const file = new File([blob], session.filename || 'unknown.session', {
            type: 'application/octet-stream',
            lastModified: Date.now()
          });
          
          // Ensure the name property is set correctly
          if (!file.name && session.filename) {
            Object.defineProperty(file, 'name', {
              value: session.filename,
              writable: false
            });
          }
          
          console.log(`Successfully created File object for ${session.filename}, size: ${file.size}`);
          console.log(`File object properties: name=${file.name}, type=${file.type}, size=${file.size}`);
          return file;
        } catch (error) {
          console.error(`Error decoding session file ${session.filename}:`, error);
          console.error('Session content length:', session.content?.length);
          console.error('Session content preview:', session.content?.substring(0, 100));
          toast.error(`Failed to decode session file ${session.filename}`);
          return null;
        }
      }).filter(Boolean); // Remove any null entries from failed decodings
      
      // Add metadata to track which files came from which ZIP
      const sessionFilesWithMetadata = sessionFileObjects.map(file => ({
        ...file,
        isFromZip: true,
        zipSource: zipFile.name
      }));
      
      setExtractedSessions(prev => [...prev, ...sessionFilesWithMetadata]);
      toast.success(`Extracted ${sessionFiles.length} session files from ${zipFile.name}`);
    } catch (error) {
      console.error('Error processing ZIP:', error);
      let errorMessage = 'Failed to process ZIP file';
      
      if (error.response?.data?.detail) {
        errorMessage = error.response.data.detail;
      } else if (error.response?.data?.message) {
        errorMessage = error.response.data.message;
      } else if (error.message) {
        errorMessage = error.message;
      }
      
      toast.error(errorMessage);
    } finally {
      setIsProcessingZip(false);
    }
  };

  const onDrop = useCallback(async (acceptedFiles, rejectedFiles) => {
    if (rejectedFiles.length > 0) {
      rejectedFiles.forEach(({ file, errors }) => {
        errors.forEach(error => {
          if (error.code === 'file-invalid-type') {
            toast.error(`${file.name} is not a valid file type`);
          } else if (error.code === 'file-too-large') {
            toast.error(`${file.name} is too large`);
          } else {
            toast.error(`${file.name}: ${error.message}`);
          }
        });
      });
    }

    if (acceptedFiles.length > 0) {
      const newFiles = multiple ? [...files, ...acceptedFiles] : acceptedFiles;
      setFiles(newFiles);
      
      // Process ZIP files
      const zipFiles = acceptedFiles.filter(file => 
        file.name.endsWith('.zip') || 
        file.type === 'application/zip' ||
        file.type === 'application/x-zip-compressed' ||
        file.type === 'application/zip-compressed' ||
        file.type === 'multipart/x-zip'
      );
      const sessionFiles = acceptedFiles.filter(file => 
        file.name.endsWith('.session') || 
        file.type === 'application/octet-stream'
      );
      
      // Add individual session files to extracted sessions
      if (sessionFiles.length > 0) {
        setExtractedSessions(prev => [...prev, ...sessionFiles]);
      }
      
      // Process ZIP files
      for (const zipFile of zipFiles) {
        await processZipFile(zipFile);
      }
      
      toast.success(`Added ${acceptedFiles.length} file(s)`);
    }
  }, [files, multiple]);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'application/zip': ['.zip'],
      'application/octet-stream': ['.session'],
      'application/x-zip-compressed': ['.zip'],
      'application/zip-compressed': ['.zip'],
      'multipart/x-zip': ['.zip']
    },
    multiple,
    maxFiles,
  });

  const removeFile = (index) => {
    const newFiles = files.filter((_, i) => i !== index);
    setFiles(newFiles);
    toast.success('File removed');
  };

  const clearAllFiles = () => {
    setFiles([]);
    setExtractedSessions([]);
    setSelectedSessions([]);
    onFilesSelected([]);
    toast.success('All files cleared');
  };

  const toggleSessionSelection = (session) => {
    setSelectedSessions(prev => {
      const isSelected = prev.some(s => s.name === session.name && s.zipSource === session.zipSource);
      if (isSelected) {
        return prev.filter(s => !(s.name === session.name && s.zipSource === session.zipSource));
      } else {
        return [...prev, session];
      }
    });
  };

  const removeExtractedSession = (sessionToRemove) => {
    setExtractedSessions(prev => 
      prev.filter(session => !(session.name === sessionToRemove.name && session.zipSource === sessionToRemove.zipSource))
    );
    setSelectedSessions(prev => 
      prev.filter(session => !(session.name === sessionToRemove.name && session.zipSource === sessionToRemove.zipSource))
    );
    toast.success('Session file removed');
  };

  const processSelectedSessions = () => {
    if (selectedSessions.length === 0) {
      toast.error('Please select at least one session file');
      return;
    }
    
    onFilesSelected(selectedSessions);
    toast.success(`Processing ${selectedSessions.length} selected session file(s)`);
  };

  const selectAllSessions = () => {
    setSelectedSessions([...extractedSessions]);
    toast.success(`Selected all ${extractedSessions.length} session files`);
  };

  const deselectAllSessions = () => {
    setSelectedSessions([]);
    toast.success('Deselected all session files');
  };

  return (
    <div className="space-y-4">
      {/* Dropzone */}
      <div
        {...getRootProps()}
        className={`border-2 border-dashed rounded-lg p-8 text-center transition-colors cursor-pointer ${
          isDragActive
            ? 'border-telegram-500 bg-telegram-50'
            : 'border-gray-300 hover:border-telegram-400 hover:bg-gray-50'
        }`}
      >
        <input {...getInputProps()} />
        <Upload className="mx-auto h-12 w-12 text-gray-400" />
        <h3 className="mt-4 text-lg font-medium text-gray-900">{title}</h3>
        <p className="mt-2 text-sm text-gray-600">{description}</p>
        {acceptedFileTypes.length > 0 && (
          <p className="mt-1 text-xs text-gray-500">
            Accepted file types: {acceptedFileTypes.join(', ')}
          </p>
        )}
        {isDragActive && (
          <p className="mt-2 text-sm text-telegram-600">Drop the files here...</p>
        )}
      </div>

      {/* Processing Indicator */}
      {isProcessingZip && (
        <div className="flex items-center justify-center p-4 bg-blue-50 border border-blue-200 rounded-lg">
          <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-blue-600 mr-3"></div>
          <span className="text-blue-800">Processing ZIP file...</span>
        </div>
      )}

      {/* Uploaded Files List */}
      {files.length > 0 && (
        <div className="space-y-3">
          <div className="flex items-center justify-between">
            <h4 className="text-sm font-medium text-gray-900">
              Uploaded Files ({files.length})
            </h4>
            <button
              onClick={clearAllFiles}
              className="text-sm text-red-600 hover:text-red-700"
            >
              Clear All
            </button>
          </div>
          
          <div className="space-y-2">
            {files.map((file, index) => (
              <div
                key={`${file.name}-${index}`}
                className="flex items-center justify-between p-3 bg-white border border-gray-200 rounded-lg"
              >
                <div className="flex items-center space-x-3">
                  {file.name.endsWith('.zip') ? (
                    <Archive className="h-5 w-5 text-orange-400" />
                  ) : (
                    <FileText className="h-5 w-5 text-gray-400" />
                  )}
                  <div>
                    <p className="text-sm font-medium text-gray-900">{file.name}</p>
                    <p className="text-xs text-gray-500">
                      {(file.size / 1024).toFixed(1)} KB
                    </p>
                  </div>
                </div>
                <button
                  onClick={() => removeFile(index)}
                  className="text-gray-400 hover:text-red-500 transition-colors"
                >
                  <X className="h-5 w-5" />
                </button>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Extracted Session Files */}
      {extractedSessions.length > 0 && (
        <div className="space-y-3">
          <div className="flex items-center justify-between">
            <h4 className="text-sm font-medium text-gray-900">
              Available Session Files ({extractedSessions.length})
            </h4>
            <div className="flex space-x-2">
              <button
                onClick={selectAllSessions}
                className="text-sm text-blue-600 hover:text-blue-700"
              >
                Select All
              </button>
              <button
                onClick={deselectAllSessions}
                className="text-sm text-gray-600 hover:text-gray-700"
              >
                Deselect All
              </button>
            </div>
          </div>
          
          <div className="space-y-2">
            {extractedSessions.map((session, index) => {
              const isSelected = selectedSessions.some(s => 
                s.name === session.name && s.zipSource === session.zipSource
              );
              
              return (
                <div
                  key={`${session.name}-${session.zipSource}-${index}`}
                  className={`flex items-center justify-between p-3 border rounded-lg transition-colors ${
                    isSelected 
                      ? 'bg-blue-50 border-blue-200' 
                      : 'bg-white border-gray-200'
                  }`}
                >
                  <div className="flex items-center space-x-3">
                    <button
                      onClick={() => toggleSessionSelection(session)}
                      className={`w-5 h-5 rounded border-2 flex items-center justify-center transition-colors ${
                        isSelected 
                          ? 'bg-blue-600 border-blue-600' 
                          : 'border-gray-300 hover:border-blue-400'
                      }`}
                    >
                      {isSelected && <Check className="w-3 h-3 text-white" />}
                    </button>
                    <FileText className="h-5 w-5 text-gray-400" />
                    <div>
                      <p className="text-sm font-medium text-gray-900">{session.name}</p>
                      <p className="text-xs text-gray-500">
                        {(session.size / 1024).toFixed(1)} KB
                        {session.isFromZip && ` • From: ${session.zipSource}`}
                      </p>
                    </div>
                  </div>
                  <button
                    onClick={() => removeExtractedSession(session)}
                    className="text-gray-400 hover:text-red-500 transition-colors"
                  >
                    <Trash2 className="h-5 w-5" />
                  </button>
                </div>
              );
            })}
          </div>
          
          {/* Process Selected Button */}
          <div className="flex justify-end pt-2">
            <button
              onClick={processSelectedSessions}
              disabled={selectedSessions.length === 0}
              className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                selectedSessions.length === 0
                  ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                  : 'bg-blue-600 text-white hover:bg-blue-700'
              }`}
            >
              Process Selected ({selectedSessions.length})
            </button>
          </div>
        </div>
      )}

      {/* Empty State */}
      {files.length === 0 && extractedSessions.length === 0 && (
        <div className="flex items-center justify-center p-4 text-gray-500">
          <AlertCircle className="h-5 w-5 mr-2" />
          <span className="text-sm">No files uploaded</span>
        </div>
      )}
    </div>
  );
};

export default FileUpload;
