import React, { useState, useEffect } from 'react';
import { Image, CheckCircle, XCircle, AlertTriangle, Loader, Upload, User } from 'lucide-react';
import FileUpload from '../components/FileUpload';
import { sessionAPI } from '../services/api';
import toast from 'react-hot-toast';

const ProfilePictureManager = () => {
  const [sessionFiles, setSessionFiles] = useState([]);
  const [profilePicture, setProfilePicture] = useState(null);
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [processingIndex, setProcessingIndex] = useState(-1);
  const [showFileUpload, setShowFileUpload] = useState(true);
  const [showPictureUpload, setShowPictureUpload] = useState(true);

  const handleSessionFilesSelected = (selectedFiles) => {
    setSessionFiles(selectedFiles);
    setResults([]);
    setShowFileUpload(false);
  };

  const handleProfilePictureSelected = (selectedFiles) => {
    if (selectedFiles.length > 0) {
      setProfilePicture(selectedFiles[0]);
      setResults([]);
      setShowPictureUpload(false);
    }
  };

  const handleUpdateProfilePictures = async () => {
    if (sessionFiles.length === 0) {
      toast.error('Please select session files');
      return;
    }

    if (!profilePicture) {
      toast.error('Please select a profile picture');
      return;
    }

    setLoading(true);
    setResults([]);
    
    try {
      // Simulate processing one account at a time for live feedback
      const tempResults = [];
      
      for (let i = 0; i < sessionFiles.length; i++) {
        setProcessingIndex(i);
        
        // Create a temporary result to show processing status
        const tempResult = {
          session: sessionFiles[i].name,
          status: 'processing',
          timestamp: new Date().toISOString()
        };
        
        tempResults.push(tempResult);
        setResults([...tempResults]);
        
        // Simulate a delay for visual feedback (remove in production)
        await new Promise(resolve => setTimeout(resolve, 500));
        
        // Make the actual API call for this session
        try {
          const singleResponse = await sessionAPI.updateProfilePictures([sessionFiles[i]], profilePicture);
          
          // Update this session's result with the actual response
          if (singleResponse.results && singleResponse.results.length > 0) {
            tempResults[i] = singleResponse.results[0];
          } else {
            // Fallback if the API doesn't return expected format
            tempResults[i] = {
              ...tempResult,
              status: 'success',
              user_id: 'Unknown',
              profile_picture: 'Updated'
            };
          }
        } catch (sessionError) {
          console.error(`Error updating session ${sessionFiles[i].name}:`, sessionError);
          tempResults[i] = {
            ...tempResult,
            status: 'error',
            error: sessionError.message || 'Failed to update profile picture'
          };
        }
        
        // Update the results after each session is processed
        setResults([...tempResults]);
      }
      
      toast.success(`Updated profile pictures for ${sessionFiles.length} sessions`);
    } catch (error) {
      console.error('Profile picture update error:', error);
      toast.error('Failed to update profile pictures. Please try again.');
    } finally {
      setLoading(false);
      setProcessingIndex(-1);
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'success':
        return <CheckCircle className="h-5 w-5 text-green-500" />;
      case 'error':
        return <XCircle className="h-5 w-5 text-red-500" />;
      case 'unauthorized':
        return <AlertTriangle className="h-5 w-5 text-yellow-500" />;
      case 'processing':
        return <Loader className="h-5 w-5 text-blue-500 animate-spin" />;
      default:
        return <AlertTriangle className="h-5 w-5 text-gray-500" />;
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'success':
        return 'bg-green-50 border-green-200';
      case 'error':
        return 'bg-red-50 border-red-200';
      case 'unauthorized':
        return 'bg-yellow-50 border-yellow-200';
      case 'processing':
        return 'bg-blue-50 border-blue-200';
      default:
        return 'bg-gray-50 border-gray-200';
    }
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center space-x-3">
        <Image className="h-8 w-8 text-telegram-500" />
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Profile Pictures</h1>
          <p className="text-gray-600">Set profile pictures for multiple accounts at once</p>
        </div>
      </div>

      {/* File Selection Step */}
      <div className="space-y-6">
        {/* Session Files Upload - Only show if no files selected or explicitly shown */}
        {(showFileUpload || !sessionFiles.length) && (
          <div className="card">
            <h2 className="text-lg font-semibold text-gray-900 mb-4">Upload Session Files</h2>
            <FileUpload
              onFilesSelected={handleSessionFilesSelected}
              acceptedFileTypes={['.session', '.zip']}
              title="Upload Session Files or ZIP"
              description="Drag & drop session files or ZIP files here, or click to select files"
            />
          </div>
        )}

        {/* Session Files Summary - Show when files are selected */}
        {sessionFiles.length > 0 && !showFileUpload && (
          <div className="card bg-blue-50 border border-blue-200">
            <div className="flex justify-between items-start">
              <div>
                <h2 className="text-lg font-semibold text-gray-900 mb-2">Session Files Selected</h2>
                <div className="text-sm text-gray-600">
                  <p>{sessionFiles.length} session file(s) ready for update</p>
                  <ul className="mt-2 list-disc list-inside">
                    {sessionFiles.slice(0, 3).map((file, index) => (
                      <li key={index} className="truncate">{file.name}</li>
                    ))}
                    {sessionFiles.length > 3 && (
                      <li>...and {sessionFiles.length - 3} more</li>
                    )}
                  </ul>
                </div>
              </div>
              <button 
                onClick={() => {
                  setShowFileUpload(true);
                }}
                className="text-sm text-blue-600 hover:text-blue-800 font-medium"
              >
                Change Files
              </button>
            </div>
          </div>
        )}

        {/* Profile Picture Upload - Only show if no picture selected or explicitly shown */}
        {(showPictureUpload || !profilePicture) && (
          <div className="card">
            <h2 className="text-lg font-semibold text-gray-900 mb-4">Upload Profile Picture</h2>
            <FileUpload
              onFilesSelected={handleProfilePictureSelected}
              acceptedFileTypes={['.jpg', '.jpeg', '.png', '.gif']}
              multiple={false}
              maxFiles={1}
              title="Upload Profile Picture"
              description="Drag & drop an image file here, or click to select"
            />
          </div>
        )}

        {/* Profile Picture Summary - Show when picture is selected */}
        {profilePicture && !showPictureUpload && (
          <div className="card bg-blue-50 border border-blue-200">
            <div className="flex justify-between items-start">
              <div className="flex items-center space-x-4">
                <div className="w-16 h-16 rounded-full overflow-hidden bg-white border-2 border-blue-300 flex items-center justify-center">
                  <img
                    src={URL.createObjectURL(profilePicture)}
                    alt="Profile preview"
                    className="w-full h-full object-cover"
                  />
                </div>
                <div>
                  <h2 className="text-lg font-semibold text-gray-900">Profile Picture Selected</h2>
                  <p className="text-sm text-gray-600">{profilePicture.name}</p>
                </div>
              </div>
              <button 
                onClick={() => {
                  setShowPictureUpload(true);
                }}
                className="text-sm text-blue-600 hover:text-blue-800 font-medium"
              >
                Change Picture
              </button>
            </div>
          </div>
        )}
        
        {/* Update Profile Pictures Button */}
        {sessionFiles.length > 0 && profilePicture && (
          <div className="card bg-green-100 border-2 border-green-500 shadow-lg">
            <h2 className="text-xl font-bold text-center text-green-800 mb-4">READY TO SET PROFILE PICTURE</h2>
            <p className="text-center text-gray-700 mb-4">This will set the selected profile picture for all {sessionFiles.length} accounts</p>
            <div className="flex justify-center">
              <button
                onClick={handleUpdateProfilePictures}
                disabled={loading}
                className="btn-primary bg-green-600 hover:bg-green-700 flex items-center space-x-2 px-10 py-4 text-xl rounded-lg shadow-md"
              >
                {loading ? (
                  <>
                    <Loader className="h-6 w-6 animate-spin" />
                    <span>SETTING PROFILE PICTURES...</span>
                  </>
                ) : (
                  <>
                    <User className="h-6 w-6" />
                    <span>SET PROFILE PICTURE NOW</span>
                  </>
                )}
              </button>
            </div>
          </div>
        )}
      </div>

      {/* Live Processing Results */}
      {(results.length > 0 || loading) && (
        <div className="card">
          <h2 className="text-lg font-semibold text-gray-900 mb-4">
            {loading ? 'Live Processing Status' : 'Update Results'} ({results.length} sessions)
          </h2>
          
          <div className="space-y-4">
            {results.map((result, index) => (
              <div
                key={index}
                className={`p-4 rounded-lg border ${getStatusColor(result.status)}`}
              >
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    {getStatusIcon(result.status)}
                    <div>
                      <h3 className="font-medium text-gray-900">{result.session}</h3>
                      <p className="text-sm text-gray-600">
                        Status: {result.status.charAt(0).toUpperCase() + result.status.slice(1)}
                        {result.status === 'processing' && ' - Setting profile picture...'}
                      </p>
                    </div>
                  </div>
                  
                  {result.status === 'success' && (
                    <div className="text-right">
                      <p className="text-sm font-medium text-gray-900">
                        User ID: {result.user_id}
                      </p>
                      <p className="text-sm text-gray-600">
                        Profile Picture: {result.profile_picture}
                      </p>
                    </div>
                  )}
                </div>

                {result.status === 'error' && (
                  <div className="mt-3">
                    <p className="text-sm font-medium text-gray-900">
                      Error: {result.error}
                    </p>
                    {result.error_type && (
                      <p className="text-xs text-gray-600 mt-1">
                        Type: {result.error_type}
                      </p>
                    )}
                  </div>
                )}

                {result.status === 'unauthorized' && (
                  <div className="mt-3">
                    <p className="text-sm text-gray-600">
                      This session is not authorized. Please check if the session file is valid.
                    </p>
                  </div>
                )}
              </div>
            ))}
            
            {/* Show a placeholder for the currently processing session */}
            {loading && processingIndex >= 0 && processingIndex < sessionFiles.length && 
             !results.some(r => r.session === sessionFiles[processingIndex].name) && (
              <div className="p-4 rounded-lg border bg-blue-50 border-blue-200">
                <div className="flex items-center space-x-3">
                  <Loader className="h-5 w-5 text-blue-500 animate-spin" />
                  <div>
                    <h3 className="font-medium text-gray-900">{sessionFiles[processingIndex].name}</h3>
                    <p className="text-sm text-gray-600">Status: Processing - Setting profile picture...</p>
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Summary */}
          <div className="mt-6 p-4 bg-gray-50 rounded-lg">
            <h3 className="font-medium text-gray-900 mb-2">Summary</h3>
            <div className="grid grid-cols-2 gap-4 text-sm">
              <div>
                <span className="text-gray-600">Total Sessions:</span>
                <span className="ml-2 font-medium">{results.length}</span>
              </div>
              <div>
                <span className="text-gray-600">Successfully Updated:</span>
                <span className="ml-2 font-medium text-green-600">
                  {results.filter(r => r.status === 'success').length}
                </span>
              </div>
              <div>
                <span className="text-gray-600">Errors:</span>
                <span className="ml-2 font-medium text-red-600">
                  {results.filter(r => r.status === 'error').length}
                </span>
              </div>
              <div>
                <span className="text-gray-600">Unauthorized:</span>
                <span className="ml-2 font-medium text-yellow-600">
                  {results.filter(r => r.status === 'unauthorized').length}
                </span>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProfilePictureManager;
